settings.custom_css:
selector .entry-header {
    display: none;
}

selector thead {
    background-color: transparent !important;
}

selector tbody tr:last-child {
    display: none;
}

selector th, td {
    text-align: center;
}

selector .schedule-input {
                width: 100%;
                padding: 5px;
                box-sizing: border-box;
            }

selector h3 {
    margin-top: 30px;
    background-color: #f5f5f5;
    padding: 15px;
    color: #23262F;

font-family: "Noto Sans TC";
font-size: 20px;
font-style: normal;
font-weight: 500;
line-height: 24px; 
}

selector .elementor-field-textual{
    border-color: 1px solid #69727d !important;
}

selector .elementor-select-wrapper .elementor-field-textual {
    padding: 0 0 0 15px !important;
    color: #8F8F8F !important;
}

selector .elementor-select-wrapper .select-caret-down-wrapper {
    display: none;
}

selector b {
    color: #8F8F8F;
}
